//Chess.Chesses 是一個靜態類別，僅提供外界得知每顆棋的基本資訊
export class Chess {
    constructor(name, rows, cols, id) {
        this.rows = 0; //這顆棋是由幾列所構成
        this.cols = 0; //這顆棋是由幾行所構成
        this.id = 0;
        this.shapeType = 0; //幾何形別，若是空格，則以 0 表示
        this.style = 0; //設定背景樣式，style 是指位於 css 檔裡的 style1、style2...
        this.name = ''; //要秀出的文字，例如【曹操孟德】
        //轉換成字元陣列 chars 
        this.chars = []; //[['曹','操'],['孟','德']]
        this.id = id;
        this.name = name;
        this.rows = rows;
        this.cols = cols;
        this.createCharsArray(name);
        this.setShapeType(rows, cols);
        this.style = id;
    }
    //轉換為字元陣列 chars[][]
    createCharsArray(nameStr) {
        let count = 0;
        for (let r = 0; r < this.rows; r++) {
            this.chars[r] = [];
            for (let c = 0; c < this.cols; c++) {
                this.chars[r][c] = nameStr[count++];
            }
        }
    }
    //依照棋子構造，決定其幾何型態，用來把同樣形狀的棋子歸為一類
    setShapeType(rows, cols) {
        let pattern = [[2, 2], [1, 2], [2, 1], [1, 1]];
        for (let shape = 0; shape < pattern.length; shape++) {
            if (rows === pattern[shape][0] && cols === pattern[shape][1]) {
                if (shape === 4)
                    alert("shape!!!");
                this.shapeType = shape;
                return;
            }
        }
        alert('無法設定 橫${rows}格，豎${cols}格，這種樣式的棋子');
        this.shapeType = 0; //預設回傳值
    }
    //設定背景樣式，style 是指位於 css 檔裡的 style1、style2...
    setStyle(style) {
        this.style = style;
    }
    //提供每一顆棋子的資訊
    static createAllChesses() {
        let chData = [
            ['曹操孟德', 2, 2],
            ['關羽', 1, 2],
            ['張飛', 2, 1],
            ['趙雲', 2, 1],
            ['馬超', 2, 1],
            ['黃忠', 2, 1],
            ['兵', 1, 1],
            ['卒', 1, 1],
            ['奴', 1, 1],
            ['僕', 1, 1],
        ];
        for (let i = 0; i < Chess.Total; i++) {
            Chess.chesses.push(new Chess(chData[i][0], chData[i][1], chData[i][2], i));
        }
    }
    //由 id 得知棋子名稱(張飛、關羽...)
    static getChessName(id) {
        return Chess.chesses[id].name;
    }
    //由 id 取出棋子
    static getChess(id) {
        return Chess.chesses[id];
    }
    //由 id 得知其幾何造型
    static getShapeType(id) {
        return Chess.chesses[id].shapeType;
    }
} //Chess
Chess.chesses = []; //所有的棋子
Chess.Total = 10; //共10顆棋子
