// 定義 dirs 並直接導出
export const Dirs = [
    { dx: 0, dy: -1 }, // 上
    { dx: -1, dy: 0 }, // 左
    { dx: 1, dy: 0 }, // 右
    { dx: 0, dy: 1 } // 下
];
// 定義 Directions 類別
export class Directions {
    // 依照 dir 傳回對應的中文方向
    static asString(dir) {
        if (dir >= 0 && dir < Directions.dirString.length) {
            return Directions.dirString[dir];
        }
        else {
            return ''; // 或者是其他的預設值
        }
    }
}
// 上0，左1，右2，下3，相反的方向和為 3
Directions.dirString = ['上', '左', '右', '下'];
