import { Game } from './game.js';
import { Sounds } from "./soundPlay.js";
//接收的網址參數，類似如下:
//index.html?level=3&name=橫刀立馬&board=202212333443
window.onload = () => {
    const domLevel = document.getElementById("whichLevel");
    const domLevelName = document.getElementById("levelName");
    //取得網址參數
    const params = new URLSearchParams(window.location.search);
    const level = params.get("level");
    const levelName = params.get("name");
    const levelStr = params.get("board");
    //更改標題
    domLevel.innerText = `【華容道】 第 ${level} 局`;
    domLevelName.innerText = levelName;
    let g = new Game();
    g.loadLevel(levelStr);
    g.drawBoard();
    g.showInfo("可以滑動棋子，開始遊戲");
    Sounds.playNewLevel();
};
